import React , { useState, useEffect } from 'react'

// import {COLUMNS} from './components/columns';
import axios from 'axios';
import DataTable from 'react-data-table-component';
import './styles.css';
import { CustomStyle } from './CustomStyle';


function App() {
    const [data, setData] = useState([]);
    const [filterData, setFilterData] = useState([]);
    const [filterText, setFilterText] = useState('');
    const [showFilteredData, setShowFilteredData] = useState(false);

    const column = [
        {
            name : "Sno",
            selector : row => row.sno
        },
        {
            name : "Name",
            selector : row => row.cust_name
        },
        {
            name : "Age",
            selector : row => row.age
        },
        {
            name : "Phone_No",
            selector : row => row.phone
        },
        {
            name : "Location",
            selector : row => row.locations
        },
        // {
        //     name: "Created At",
        //     selector: "created_at" // New grouped column
        // },
        // {
        //     name : "Date",
        //     selector : row => (row.dates).substring(0,10),
        //     sortable: true
        // },
        // {
        //     name : "Time",
        //     selector : row => row.times,
        //     sortable: true
        // },

    ]


    useEffect(() => {
        fetchCustomers();

    }, []);
    console.log(data);
    const fetchCustomers = async () => {
        try {
            axios.get('http://localhost:4000/api/customer_details')
            .then(response => {
                setData(response.data);
                setFilterData(response.data);
            })
            .catch(err => console.log(err));

        } catch (error) {
            console.error('Error fetching customers:', error);
        }
    };
    // const changeHandler = (e) =>{
    //     const newdata = filterData.filter(row => row.cust_name.toLowerCase().includes(e.target.value.toLowerCase()));
    //     setData(newdata);
    // }
    const changeHandler = (e) => {
        const searchText = e.target.value.toLowerCase();
        const filteredData = data.filter(row => row.cust_name.toLowerCase().includes(searchText));
        setFilterText(searchText);
        setShowFilteredData(true);
        setFilterData(filteredData);
    };
    const toggleFilter = () => {
        setShowFilteredData(!showFilteredData);
        if (!showFilteredData) {
            setFilterData(data.filter(row => row.cust_name.toLowerCase().includes(filterText)));
        } else {
            setFilterData(data);
        }
    };
    // const sortByDate = () => {
    //     const sortedData = [...filterData].sort((a, b) => new Date(a.dates) - new Date(b.dates));
    //     setFilterData(sortedData);
    // };

    // const sortByTime = () => {
    //     const sortedData = [...filterData].sort((a, b) => new Date('1970/01/01 ' + a.times) - new Date('1970/01/01 ' + b.times));
    //     setFilterData(sortedData);
    // };
    const sortByDate = () => {
        const sortedData = [...filterData].sort((a, b) => {
            const dateA = new Date(a.dates);
            const dateB = new Date(b.dates);
            return dateA - dateB;
        });
        setFilterData(sortedData);
    };

    const sortByTime = () => {
        const sortedData = [...filterData].sort((a, b) => {
            const timeA = new Date('1970/01/01 ' + a.times);
            const timeB = new Date('1970/01/01 ' + b.times);
            return timeA - timeB;
        });
        setFilterData(sortedData);
    };

  return (

    <div style={{padding : "50px 10%",backgroundColor : "gray"}}>
        <div className='heading'> <h2>Customer-Details</h2></div>
        <div className='searchContainer'>
            <input type='text' placeholder='Search...' onChange={changeHandler} className='searchBox'/>
            <button onClick={sortByDate}>Sort by Date</button>
            <button onClick={sortByTime}>Sort by Time</button>
            <button onClick={toggleFilter}>Toggle Filter</button>
        </div>
        <DataTable
            columns = {column}
            data = {data}
            pagination
            paginationPerPage={20}
            customStyles={CustomStyle}
            // customStyles={require('./styles.css')}
        >
        </DataTable>
    </div>
  )
}

export default App
