import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styling.css'; // External CSS file for styling

function App() {
    const [data, setData] = useState([]);
    const [filterText, setFilterText] = useState('');
    const [showFilteredData, setShowFilteredData] = useState(false);

    useEffect(() => {
        fetchCustomers();
    }, []);

    const fetchCustomers = async () => {
        try {
            const response = await axios.get('http://localhost:4000/api/customer_details');
            setData(response.data);
        } catch (error) {
            console.error('Error fetching customers:', error);
        }
    };

    const filteredData = data.filter(row => row.cust_name.toLowerCase().includes(filterText.toLowerCase()));

    const toggleFilter = () => {
        setShowFilteredData(!showFilteredData);
    };

    return (
        <div style={{ padding: "50px 10%", backgroundColor: "gray" }}>
            <div className='heading'><h2>Customer-Details</h2></div>
            <div className='searchContainer'>
                <input type='text' placeholder='Search...' onChange={e => setFilterText(e.target.value)} className='searchBox' />
                <button onClick={toggleFilter}>Toggle Filter</button>
            </div>
            <table className="custom-table">
                <thead>
                    <tr>
                        <th>Sno</th>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Phone No</th>
                        <th>Location</th>
                        <th colspan="2">Created At

                        </th>
                        <th>Created At</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.sno}</td>
                            <td>{row.cust_name}</td>
                            <td>{row.age}</td>
                            <td>{row.phone}</td>
                            <td>{row.locations}</td>
                            <td>{row.dates.substring(0, 10)} {row.times}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default App;
