            <input type='text' placeholder='Search...' onChange={changeHandler} className='searchBox'/>

        </div>
        <DataTable
            columns = {column}
            data = {data}
            pagination
            paginationPerPage={20}
            // customStyles={customStyles}
            v
        >
        </DataTable>
    </div>
  )
}

export default App
