import React , { useState, useEffect } from 'react'

// import {COLUMNS} from './components/columns';
import axios from 'axios';
import DataTable from 'react-data-table-component';
import './styles.css';


function App() {
    // const columns = useMemo(() => COLUMNS, []);
    // const customStyles = {
    //     headRow : {
    //         style :{
    //             backgroundColor : 'blue',
    //             color : 'white'
    //         }
    //     }
    // }
    const customStyles = {
        headRow: {
            style: {
                backgroundColor: '#007bff',
                color: 'white',
                fontWeight: 'bold',
            }
        },
        headCells: {
            style: {
                paddingLeft: '8px', // Adjust cell padding as needed
                paddingRight: '8px',
                borderRight: '1px solid #dee2e6',
                textAlign: 'center',
            }
        },
        rows: {
            style: {
                minHeight: '56px', // Adjust row height as needed
            }
        },
        cells: {
            style: {
                paddingLeft: '8px', // Adjust cell padding as needed
                paddingRight: '8px',
                borderRight: '1px solid #dee2e6',
                textAlign: 'center',
            }
        },
        pagination: {
            style: {
                backgroundColor: '#007bff', // Adjust pagination background color
                color: 'white',
            }
        },
        rowStripes: {
            oddRowColor: '#f0f0f0', // light gray color
            evenRowColor: 'blue',
        },
    };

    const column = [
        {
            name : "Sno",
            selector : row => row.sno
        },
        {
            name : "Name",
            selector : row => row.cust_name
        },
        {
            name : "Age",
            selector : row => row.age
        },
        {
            name : "Phone_No",
            selector : row => row.phone
        },
        {
            name : "Location",
            selector : row => row.locations
        },
        {
            name : "Date",
            selector : row => (row.dates).substring(0,10),
            sortable: true
        },
        {
            name : "Time",
            selector : row => row.times,
            sortable: true
        },
    ]
    const [data, setData] = useState([]);
    const [filterData, setFilterData] = useState([]);
    useEffect(() => {
        fetchCustomers();

    }, []);
    console.log(data);
    const fetchCustomers = async () => {
        try {
            axios.get('http://localhost:4000/api/customer_details')
            .then(response => {
                setData(response.data);
                setFilterData(response.data);
            })
            .catch(err => console.log(err));

        } catch (error) {
            console.error('Error fetching customers:', error);
        }
    };
    const changeHandler = (e) =>{
        const newdata = filterData.filter(row => row.cust_name.toLowerCase().includes(e.target.value.toLowerCase()));
        setData(newdata);
    }
  console.log(row => row.dates)
  return (

    <div style={{padding : "50px 10%",backgroundColor : "gray"}}>
        <div>Customer-Details<h2></h2></div>
        <div>
            <input type='text' placeholder='Search...' onChange={changeHandler}/>

        </div>
        <DataTable
            columns = {column}
            data = {data}
            pagination
            customStyles={customStyles}
        >
        </DataTable>
    </div>
  )
}

export default App
